import { useCallback, useState } from "react";

// General GET request
export function useGet(URL, dataMapping = (e) => e) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchData = useCallback(async () => {
    setLoading(true);
    let results;
    try {
      const rsp = await fetch(URL);
      if (!rsp.ok) {
        throw new Error("Something Went Wrong");
      }

      const data = await rsp.json();
      results = data.results.map((data) => dataMapping(data));
    } catch (err) {
      console.log(err);
      setError(err);
      setLoading(false);
      return;
    }
    setLoading(false);
    return results;
  }, [URL,dataMapping]);

  return [
    fetchData,
    loading,
    { setLoading: setLoading, fetchedData: fetchData(), error: error },
  ];
}

// GET request firebase specific
// dataMapping is a function which input is the response's individual object and returns results's individual element
export function useGetFB(URL, dataMapping = (obj, key) => obj) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchData = useCallback(async () => {
    let results = [];
    try {
      const rsp = await fetch(URL);
      if (!rsp.ok) {
        throw new Error("Something Went Wrong");
      }

      const data = await rsp.json();

      for (let key in data) {
        results.push(dataMapping(data[key], key));
      }
    } catch (err) {
      console.log(err);
      setError(err);
      setLoading(false);
      return;
    }
    setLoading(false);
    return results;
  }, [URL,dataMapping]);

  return [
    fetchData,
    loading,
    { setLoading: setLoading, fetchedData: fetchData(), error: error },
  ];
}

// POST request firebase specific
export function usePostFB(URL) {
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const postData = useCallback(async (data) => {
    try {
      const rsp = await fetch(URL, {
        method: "POST",
        headers: {
          "content-type": "application/JSON",
        },
        body: data,
      });

      if (!rsp.ok) {
        throw new Error("Something Went Wrong");
      }
    } catch (err) {
      setError(err);
      setLoading(false);
      console.log(err);
    }
    setLoading(false);
  },[]);

  return [postData, loading, error];
}
