import react from "react";
import { useEffect, useState } from "react";

const AuthContext = react.createContext({
  isLoggedIn: false,
  username: "",
  onLogin: () => {},
  onLogout: () => {},
  onSignup: ()=> {}
});

export const AuthContextProvider = (props) => {
  const [authInfo, setAuthInfo] = useState({
    isLoggedIn: true,
    username: "",
  });

  const userList = JSON.parse(localStorage.getItem("userList"))

  const onLoginHandler = () => {
    localStorage.setItem("isLoggedIn", true);
    setAuthInfo((prevState) => {
      console.log(prevState)
      return {
        ...prevState,
        isLoggedIn: true,
      };
    });
    console.log(authInfo)
  };

  const onLogoutHandler = () => {
    console.log("lol u nub")
    localStorage.setItem("isLoggedIn", false);
    localStorage.setItem("username", "");
    setAuthInfo((prevState) => {
      return {
        ...prevState,
        isLoggedIn: false,
      };
    });
    console.log(authInfo)
  };
  
  const signupHandler = (username,password) => {
    const newUserList = [...userList, {username: username,password:password}]
    localStorage.setItem(JSON.stringify(newUserList))
  }

  useEffect(() => {
    setAuthInfo({
      isLoggedIn: localStorage.getItem("isLoggedin"),
      username: localStorage.getItem("username"),
    });
  },[]);

  return (
    <AuthContext.Provider value={{...authInfo, onLogin:onLoginHandler, onLogout:onLogoutHandler, on}}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
