import Button from "../UI/Button";
import style from "./LoginSignup.module.css";
import AuthContext from "../../Contexts/AuthContext";
import { useContext } from "react";
import FormLabelInput from "../UI/FormLabelInput";

const LoginForm = (props) => {
  const Authctx = useContext(AuthContext)

  const onLoginHandler = (event) => {
    event.preventDefault()
    Authctx.onLogin()
  }
  return (
    <div className={style["container"]}>
      <div className={style["form-title"]}>Welcome!</div>
      <form className={style["form-control"]}>
        <FormLabelInput label="Username"/>
        <FormLabelInput label="Password" inputType="password"/>
        <div className={style["form-control-buttons-wrapper"]}>
          <div className={style["form-control-buttons"]}>
            <Button type="submit" onClick={onLoginHandler}>Log In</Button> <span>OR</span>{" "}
            <Button>Sign Up</Button>
          </div>
        </div>
      </form>
      <div className={`${style["form-notif"]} ${Authctx.validation === "valid"? "" : style["invalid"]}`}></div>
    </div>
  );
};

export default LoginForm;
