import { useContext, useEffect, useState } from "react"
import PopupContext from "../../../Storage/PopupContext"
import style from "./CheckoutUI.module.css"

const CheckoutUI = props => {
    const PopupCtx = useContext(PopupContext)
    // State and logic for animation
    const [deliveryAnimationStage,setDeliveryAnimationStage] = useState(0)
    const [deliveryAnimationText,setDeliveryAnimationText] = useState("")
    useEffect(() => {
        if (deliveryAnimationStage === 3) {
            setDeliveryAnimationText(`Checkout Completed, Delivering in ${deliveryAnimationStage}`)
            setTimeout(() => {
                setDeliveryAnimationStage(deliveryAnimationStage - 1)
            })
        } else if (deliveryAnimationStage > 0) {
            setDeliveryAnimationText(`Delivering in ${deliveryAnimationStage}`)
            setTimeout(() => {
                setDeliveryAnimationStage(deliveryAnimationStage - 1)
            })
        } else {
            setDeliveryAnimationText("Food Delivered!")
            setTimeout(() => {
                
            })
        }
        return
    },[deliveryAnimationStage])    


    function yesHandler() {
        setDeliveryAnimationStage(3)
        PopupCtx.triggerPopup(<div className={style.loadContainer}>{deliveryAnimationText}</div>)
    }

    return <div className={style["wrapper"]}>
        <div className={style["container"]}>
            <h1 className={style["title"]}>
                Checkout order? 
            </h1>
            <h1 className={style["price"]}>
                $ {props.total}
            </h1>
            <button className={style['btn-yes']} onClick={yesHandler}>Yes</button>
            <button className={style['btn-no']} onClick={PopupCtx.disablePopup}>No</button>
        </div>

    </div>
}

export default CheckoutUI
