import Header from "./Header";
import { FoodContextProvider } from "../../Storage/FoodContext";
import Foods from "./Foods";
import TrolleyBar from "./Trolley/TrolleyBar";
import { useState,useEffect } from "react";

const MainPage = (props) => {
  const [dropActive, setDropActive] = useState("0000")

  const toggleDropActive = id => {
    setDropActive((prevState) => {
      if (prevState === id) {
        return ("0000")
      }
      return id
    })
  }

  const toggleSelfClick = () => {
    const currentDropActive = dropActive
    const timer = setTimeout(() => setDropActive("0000"), 2000)
    setTimeout( () => {
    if (currentDropActive !== dropActive) {
      clearTimeout(timer)
    }},1000)
  }

  return (
    <div onClick={toggleSelfClick}>
      <Header />
      <FoodContextProvider>
        <Foods dropActive={dropActive} setDropActive={(id) => toggleDropActive(id)}/>
        <TrolleyBar/>
      </FoodContextProvider>
    </div>
  );
};

export default MainPage;
