import react from "react";
import { useEffect,useState } from "react";

const AuthContextZero = {
    isLoggedIn: false,
    username: "",
  }

const AuthContext = react.createContext(AuthContextZero);

export const AuthContextProvider = (props) => {
    const [authInfo,setAuthInfo] = useState(AuthContextZero)

  useEffect(() => {
      setAuthInfo({
    isLoggedIn: localStorage.getItem("isLoggedn"),
    username: localStorage.getItem("username") 
      })}, []);

      const onLoginHandler = () => {
        setAuthInfo((prevState) => {return {...prevState, isLoggedIn: localStorage.getItem}})
      }

  return (
    <AuthContext.Provider value={authInfo}>
      {props.children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
